_numero($risposta_scelta); // 1 se è corretta\r\n					} else {\r\n						$risposte_serializzate.=\' \'; // spazio se non rispondo\r\n					}\r\n				}\r\n				\r\n				//Trovo il totale punti massimo, le soglie etc.. e calcolo l\'esito \r\n				$massimo_punteggio = db_query(\"SELECT \r\n											SUM(d.punteggio) AS totale \r\n										FROM test_associazione_domande a \r\n										INNER JOIN test_domande d ON (d.id_domanda=a.id_domanda AND d.record_attivo=1) \r\n										WHERE a.record_attivo=1 AND a.id_test=\".$id_apprendimento);\r\n				$massimo_punteggio = $massimo_punteggio->fetch();  \r\n				$massimo_punteggio = $massimo_punteggio->totale;\r\n				$soglia_totale = ($massimo_punteggio * $result_eventi->soglia_percentuale) / 100;\r\n				//echo \'<br>\'.$massimo_punteggio.\' * \'.$result_eventi->soglia_percentuale.\' / 100<br>\';\r\n				//echo \'<br>\'.$totale.\' >= \'.$soglia_totale.\'<br>\';\r\n				$esito=\'Non Superato\';\r\n				if ($totale>=$soglia_totale){\r\n					$esito=\'Superato\';\r\n				}\r\n\r\n                if (isset($_SERVER[\"HTTP_X_FORWARDED_FOR\"]) AND strlen($_SERVER[\"HTTP_X_FORWARDED_FOR\"]) > 0) { \r\n                        $query_IP = \", remote_IP=\'\".$_SERVER[\'REMOTE_ADDR\'].\"\', proxy_IP=\'\".$_SERVER[\'HTTP_X_FORWARDED_FOR\'].\"\'\";\r\n                } else {\r\n                        $query_IP = \", remote_IP=\'\".$_SERVER[\'REMOTE_ADDR\'].\"\'\";\r\n                }\r\n\r\n				$sql = \"\r\n					UPDATE test_risultato SET data_termine=NOW(), punteggio=\".$totale.\", punteggio_massimo=\".$massimo_punteggio.\", risposte=\'\".$risposte_serializzate.\"\', esito=\'\".$esito.\"\'\".$query_IP.\" \r\n					WHERE id_risultato=\".$esiste_apprendimento->id_risultato.\"; \r\n				\";\r\n				//echo $sql;\r\n				db_query($sql);\r\n			\r\n				//echo \'SALVATO:\'.$esiste_apprendimento->id_risultato;\r\n				/////INVIO MAIL ESITO\r\n/*\r\n				$obj_mail=new stdClass();\r\n				$obj_mail->evento=$result_eventi;\r\n				$obj_mail->discente=$result_utente;\r\